
$(function () {


	$('#comment-crousel').owlCarousel({
	    loop: true,
	    margin: 10,
	    dots: true,
	    nav: false,
	    items: 1,
	    touchDrag: false,
	    mouseDrag: false,
		autoplay: true,
	    autoplayTimeout: 5000,
	    autoplayHoverPause: true
    });


	$('.__js-scroll-form').click(function () {
		var $form = $(".js-scroll-form").offset().top;
		$("html, body").animate({ scrollTop: $form }, 1000);
	});

});
